<?phpfunction my_scripts() {	wp_register_script( 'joehoe-js', get_stylesheet_directory_uri() . '/js/joehoe.js', array('jquery'), '20160222', false );	wp_enqueue_script( 'joehoe-js' );}add_action( 'wp_enqueue_scripts', 'my_scripts' );add_post_type_support( 'page', 'excerpt' );/** * Overrides the original function to display an optional post thumbnail. * * Wraps the post thumbnail in an anchor element on index views, or a div * element when on single views. * * Create your own twentysixteen_post_thumbnail() function to override in a child theme. * * @since Twenty Sixteen 1.0 */function twentysixteen_post_thumbnail() {	if ( post_password_required() || is_attachment() || ! has_post_thumbnail() ) {		return;	}	if ( is_singular() ) :	?>	<div class="post-thumbnail">		<?php the_post_thumbnail(); ?>	</div><!-- .post-thumbnail -->	<?php else : ?>	<?php	$thumb_url = get_the_post_thumbnail_url();	if ($thumb_url) {		?>		<a class="post-thumbnail" href="<?php the_permalink(); ?>" aria-hidden="true" style="background-image:url(<?= $thumb_url ?>);">&nbsp;</a>		<?php	}	?>	<?php endif; // End is_singular()}